<?php
defined('_JEXEC') or die ;

jimport('joomla.plugin.plugin');

class plgSystemZoearth_ShowCode extends JPlugin
{
    function onAfterRender()
    {
		static $showed;
        $response = JResponse::getBody();
        //可能有 prettyprint 的 class
        if (strpos($response,'prettyprint') && !$showed)
        {
			$showed = TRUE;
            //載入JS CSS
			$addText = '
<script src="//google-code-prettify.googlecode.com/svn/loader/run_prettify.js"></script>
<style type="text/css">
.post .codeblock {
	display: block; /* fixes a strange ie margin bug */
	font-family: Courier New;
	font-size: 10pt;
	overflow:auto;
	background: #f0f0f0 url(data:image/gif;base64,) left top repeat-y;
	border: 1px solid #ccc;
	padding: 10px 10px 10px 21px;
	max-height:1000px;
	line-height: 1.2em;
}
</style>
';
			$response = JResponse::getBody();
			JResponse::setBody(str_replace('</head>',$addText.'</head>',$response));
        }
    }
}